  };

  return (
    <form onSubmit={handleSearch}>
      <input placeholder="Откуда" value={from} onChange={(e) => setFrom(e.target.value)} required />
      <input placeholder="Куда" value={to} onChange={(e) => setTo(e.target.value)} required />
      <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
      <button type="submit">Найти</button>
    </form>
  );
};

export default SearchForm;
