import React, { useState } from 'react';
import SearchForm from './components/SearchForm';
import TrainResults from './components/TrainResults';
import { mockTrains } from './mockData';

function App() {
  const [searchParams, setSearchParams] = useState(null);
  const [results, setResults] = useState([]);

  const handleSearch = (params) => {
    setSearchParams(params);
    // Фильтрация данных по направлениям и дате
    const filtered = mockTrains.filter(train =>
      train.from === params.from &&
      train.to === params.to &&
      train.date === params.date
    );
    setResults(filtered);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'sans-serif' }}>
      <h1>Бронирование билетов</h1>
      <SearchForm onSearch={handleSearch} />
      <TrainResults results={results} />
    </div>
  );
}

export default App;
