import React, { useState } from 'react';
import './TrainResults.css';

const TrainResults = ({ results }) => {
  const [hoveredSeat, setHoveredSeat] = useState(null);

  const handleMouseEnter = (seat) => {
    setHoveredSeat(seat);
  };

  const handleMouseLeave = () => {
    setHoveredSeat(null);
  };

  return (
    <div>
      <h2>Результаты поиска</h2>

      {results.map((train, index) => (
        <div key={index} className="train-card">
          <p><strong>Поезд:</strong> {train.number}</p>
          <p><strong>Маршрут:</strong> {train.from} → {train.to}</p>
          <div className="seat-map">
            {train.seats.map((seat, i) => {
              const isSide = seat.isSide;
              const isUpper = seat.isUpper;
              const isBooked = seat.booked;
              const seatClass = `
                seat 
                ${isBooked ? 'booked' : 'available'} 
                ${isUpper ? 'upper' : 'lower'} 
                ${isSide ? 'side' : ''}
              `;

              return (
                <div
                  key={i}
                  className={seatClass}
                  onMouseEnter={() => handleMouseEnter(seat)}
                  onMouseLeave={handleMouseLeave}
                >
                  {seat.number}
                  {hoveredSeat === seat && (
                    <div className="tooltip">
                      <strong>{isBooked ? 'Занято' : 'Свободно'}</strong><br />
                      {isUpper ? 'Верхнее' : 'Нижнее'}<br />
                      {seat.price} Руб.
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      ))}
    </div>
  );
};

export default TrainResults;
