import React from 'react';

const seatColors = {
  купе: '#4caf50',
  плацкарт: '#2196f3',
  люкс: '#ff9800'
};

const TrainResults = ({ results }) => {
  if (!results || results.length === 0) return <p>Нет поездов.</p>;

  return (
    <div>
      <h2>Результаты поиска</h2>
      {results.map((train, index) => (
        <div key={index} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
          <p><strong>Поезд:</strong> {train.number}</p>
          <p><strong>Маршрут:</strong> {train.from} → {train.to}</p>
          <p><strong>Дата:</strong> {train.date}</p>
          <p><strong>Отправление:</strong> {train.departure}, <strong>Прибытие:</strong> {train.arrival}</p>
          <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
            {train.seats.map((seat, i) => (
              <div key={i}
                   style={{
                     padding: '8px',
                     backgroundColor: seat.booked ? '#aaa' : seatColors[seat.type],
                     borderRadius: '4px',
                     color: '#fff'
                   }}>
                {seat.id} ({seat.type}) {seat.booked ? '⛔' : '✅'}
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default TrainResults;
