import React, { useState } from 'react';

const generateSeats = () => {
  const rows = 4;
  const cols = 5;
  const seats = [];
  for (let r = 0; r < rows; r++) {
    for (let c = 0; c < cols; c++) {
      seats.push({ id: `${r}-${c}`, selected: false });
    }
  }
  return seats;
};

const SeatSelectionModal = ({ onClose, onConfirm, train }) => {
  const [seats, setSeats] = useState(generateSeats());

  const toggleSeat = (id) => {
    setSeats(seats.map(seat =>
      seat.id === id ? { ...seat, selected: !seat.selected } : seat
    ));
  };

  const handleConfirm = () => {
    const selected = seats.filter(seat => seat.selected);
    alert(`Вы выбрали ${selected.length} мест: ${selected.map(s => s.id).join(', ')}`);
    onConfirm(selected);
    onClose();
  };

  return (
    <div style={modalStyle.overlay}>
      <div style={modalStyle.modal}>
        <h3>Выбор мест для поезда {train.number}</h3>
        <div style={modalStyle.seatGrid}>
          {seats.map(seat => (
            <div
              key={seat.id}
              style={{
                ...modalStyle.seat,
                backgroundColor: seat.selected ? '#4caf50' : '#ddd'
              }}
              onClick={() => toggleSeat(seat.id)}
            >
              {seat.id}
            </div>
          ))}
        </div>
        <button onClick={handleConfirm}>Забронировать</button>
        <button onClick={onClose} style={{ marginLeft: '10px' }}>Закрыть</button>
      </div>
    </div>
  );
};

const modalStyle = {
  overlay: {
    position: 'fixed', top: 0, left: 0, width: '100%',
    height: '100%', backgroundColor: 'rgba(0,0,0,0.5)',
    display: 'flex', justifyContent: 'center', alignItems: 'center',
    zIndex: 1000
  },
  modal: {
    backgroundColor: 'white', padding: '20px',
    borderRadius: '8px', width: '400px'
  },
  seatGrid: {
    display: 'grid', gridTemplateColumns: 'repeat(5, 1fr)',
    gap: '10px', marginBottom: '20px'
  },
  seat: {
    padding: '10px', textAlign: 'center',
    border: '1px solid #888', borderRadius: '4px',
    cursor: 'pointer'
  }
};

export default SeatSelectionModal;
