import React, { useState } from 'react';
import { fakeApi } from '../api/api';

const SearchForm = ({ onSearch }) => {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [date, setDate] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    const normalize = (s) => (s || '').trim().toLowerCase();

    const filtered = fakeApi.filter((train) =>
      (!from || normalize(train.from) === normalize(from)) &&
      (!to || normalize(train.to) === normalize(to)) &&
      (!date || train.date === date)
    );

    // Возвращаем не только поезда, но и фильтры
    onSearch(filtered, { from, to, date });
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
      <div>
        <label>Откуда: </label>
        <input type="text" value={from} onChange={(e) => setFrom(e.target.value)} />
      </div>
      <div>
        <label>Куда: </label>
        <input type="text" value={to} onChange={(e) => setTo(e.target.value)} />
      </div>
      <div>
        <label>Дата: </label>
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
      </div>
      <button type="submit">Найти</button>
    </form>
  );
};

export default SearchForm;
