export const fakeApi = {
  login: async ({ email, password }) => {
    if (email === 'test@example.com' && password === '1234') {
      return { token: 'fake-jwt-token', user: { email } };
    } else {
      throw new Error('Неверный логин или пароль');
    }
  },

  getTrainData: async () => {
  return [
    {
      number: '045А',
      from: 'Рузаевка',
      to: 'Москва',
      date: '2025-07-17',
      seats: [
        { number: 1, isUpper: false, isSide: false, bookedSegments: ['Рузаевка → Пенза'] },
        { number: 2, isUpper: true, isSide: false, bookedSegments: [] },
        { number: 3, isUpper: false, isSide: false, bookedSegments: ['Пенза → Москва'] },
        { number: 4, isUpper: true, isSide: false, bookedSegments: [] },
        { number: 21, isUpper: false, isSide: true, bookedSegments: [] },
        { number: 22, isUpper: true, isSide: true, bookedSegments: ['Рузаевка → Москва'] },
        { number: 23, isUpper: false, isSide: true, bookedSegments: [] },
        { number: 24, isUpper: true, isSide: true, bookedSegments: [] }
      ]
    }
  ];
}




};
