export const fakeApi = {
  login: async ({ email, password }) => {
    if (email === 'test@example.com' && password === '1234') {
      return { token: 'fake-jwt-token', user: { email } };
    } else {
      throw new Error('Неверный логин или пароль');
    }
  },

  getTrainData: async ({ from, to, date }) => {
  return [
    {
      number: '001А',
      from,
      to,
      date,
      departure: '08:00',
      arrival: '16:00',
      seats: [
        {
          id: '1A',
          type: 'купе',
          bookedSegments: ['Рузаевка-Пенза']
        },
        {
          id: '1B',
          type: 'купе',
          bookedSegments: []
        },
        {
          id: '2A',
          type: 'плацкарт',
          bookedSegments: ['Пенза-Москва']
        }
      ]
    }
  ];
}

};
