// src/api/api.js

export const fakeApi = {
  getTrains: async () => {
    return [
      {
        number: '001A',
        from: 'Рузаевка',
        to: 'Москва',
        date: '2025-07-17',
        seats: [
          { number: 1, isUpper: false, isSide: false, bookedSegments: ['Рузаевка → Пенза'] },
          { number: 2, isUpper: true, isSide: false, bookedSegments: [] },
          { number: 3, isUpper: false, isSide: false, bookedSegments: ['Пенза → Москва'] },
          { number: 4, isUpper: true, isSide: false, bookedSegments: [] },
          { number: 5, isUpper: false, isSide: true, bookedSegments: [] },
          { number: 6, isUpper: true, isSide: true, bookedSegments: ['Рузаевка → Москва'] },
        ],
      },
      {
        number: '002Б',
        from: 'Самара',
        to: 'Москва',
        date: '2025-07-18',
        seats: [
          { number: 1, isUpper: false, isSide: false, bookedSegments: [] },
          { number: 2, isUpper: true, isSide: false, bookedSegments: [] },
        ],
      },
    ];
  },
};
