import React, { useState } from 'react';
import './TrainResults.css';

const TrainResults = ({ results, filters }) => {
  const [selectedSeat, setSelectedSeat] = useState(null);
  const [selectedSeatInfo, setSelectedSeatInfo] = useState(null);

  const handleSeatClick = (seat, train) => {
    setSelectedSeat(seat.number);

    const allSegments = getRouteSegments(train.from, train.to);
    const busySegments = seat.bookedSegments || [];
    const freeSegments = allSegments.filter(seg => !busySegments.includes(seg));

    setSelectedSeatInfo({
      seat,
      train,
      busySegments,
      freeSegments
    });
  };

  const getRouteSegments = (from, to) => {
    const stations = [from, 'Пенза', to];
    const segments = [];
    for (let i = 0; i < stations.length - 1; i++) {
      segments.push(`${stations[i]} → ${stations[i + 1]}`);
    }
    return segments;
  };

  const isMatchingTrain = (train) => {
    return (
      (!filters || !filters.from || filters.from === train.from) &&
      (!filters || !filters.to || filters.to === train.to) &&
      (!filters || !filters.date || filters.date === train.date)
    );
  };

  return (
    <div>
      <h2>Результаты поиска</h2>

      {results.filter(isMatchingTrain).map((train, index) => (
        <div key={index} className="train-card">
          <p><strong>Поезд:</strong> {train.number}</p>
          <p><strong>Маршрут:</strong> {train.from} → {train.to}</p>
          <p><strong>Дата:</strong> {train.date}</p>

          <div className="wagon-layout">
            <div className="main-section">
              {train.seats
                .filter(seat => !seat.isSide)
                .map((seat, i) => {
                  const isBooked = (seat.bookedSegments || []).length > 0;
                  const seatClass = `
                    seat 
                    ${isBooked ? 'booked' : 'available'} 
                    ${seat.isUpper ? 'upper' : 'lower'} 
                    ${selectedSeat === seat.number ? 'selected' : ''}
                  `;

                  return (
                    <div
                      key={i}
                      className={seatClass}
                      onClick={() => handleSeatClick(seat, train)}
                    >
                      {seat.number}
                    </div>
                  );
                })}
            </div>

            <div className="side-section">
              {train.seats
                .filter(seat => seat.isSide)
                .map((seat, i) => {
                  const isBooked = (seat.bookedSegments || []).length > 0;
                  const seatClass = `
                    seat side 
                    ${isBooked ? 'booked' : 'available'} 
                    ${seat.isUpper ? 'upper' : 'lower'} 
                    ${selectedSeat === seat.number ? 'selected' : ''}
                  `;

                  return (
                    <div
                      key={i}
                      className={seatClass}
                      onClick={() => handleSeatClick(seat, train)}
                    >
                      {seat.number}
                    </div>
                  );
                })}
            </div>
          </div>

          {selectedSeatInfo && selectedSeatInfo.train.number === train.number && (
            <div className="seat-info">
              <h3>Место: {selectedSeatInfo.seat.number}</h3>
              <p><strong>Тип:</strong> {selectedSeatInfo.seat.isSide ? 'Боковое' : 'Основное'}, {selectedSeatInfo.seat.isUpper ? 'Верхнее' : 'Нижнее'}</p>
              <p><strong>Свободно на отрезках:</strong></p>
              <ul>
                {selectedSeatInfo.freeSegments.map((seg, idx) => <li key={idx}>{seg}</li>)}
              </ul>
              <p><strong>Занято на отрезках:</strong></p>
              <ul>
                {selectedSeatInfo.busySegments.map((seg, idx) => <li key={idx}>{seg}</li>)}
              </ul>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default TrainResults;
