import React, { useState } from 'react';

const seatColors = {
  купе: '#4caf50',
  плацкарт: '#2196f3',
  люкс: '#ff9800'
};

const TrainResults = ({ results }) => {
  const [selectedSeatInfo, setSelectedSeatInfo] = useState(null);

  const handleSeatClick = (seat, train) => {
    const totalRoute = `${train.from} → ${train.to}`;
    const allSegments = getRouteSegments(train.from, train.to);
    const freeSegments = allSegments.filter(seg => !seat.bookedSegments.includes(seg));
    const busySegments = seat.bookedSegments;

    setSelectedSeatInfo({
      seat,
      train,
      busySegments,
      freeSegments
    });
  };

  // Разбиваем маршрут на сегменты
  const getRouteSegments = (from, to) => {
    // Пример: [Рузаевка, Пенза, Москва]
    const stations = [from, 'Пенза', to];
    const segments = [];
    for (let i = 0; i < stations.length - 1; i++) {
      segments.push(`${stations[i]}-${stations[i + 1]}`);
    }
    return segments;
  };

  return (
    <div>
      <h2>Результаты поиска</h2>

      {results.map((train, index) => (
        <div key={index} style={{ border: '1px solid #ccc', margin: '20px 0', padding: '10px' }}>
          <p><strong>Поезд:</strong> {train.number}</p>
          <p><strong>Маршрут:</strong> {train.from} → {train.to}</p>
          <p><strong>Дата:</strong> {train.date}</p>
          <p><strong>Отправление:</strong> {train.departure}, <strong>Прибытие:</strong> {train.arrival}</p>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(4, 60px)',
            gap: '10px',
            marginTop: '10px'
          }}>
            {train.seats.map((seat, i) => {
              const isBooked = seat.bookedSegments.length > 0;

              return (
                <div
                  key={i}
                  onClick={() => handleSeatClick(seat, train)}
                  style={{
                    padding: '10px',
                    backgroundColor: isBooked ? '#888' : seatColors[seat.type],
                    borderRadius: '4px',
                    color: 'white',
                    textAlign: 'center',
                    cursor: 'pointer'
                  }}
                >
                  {seat.id}
                </div>
              );
            })}
          </div>
        </div>
      ))}

      {selectedSeatInfo && (
        <div style={{
          marginTop: '20px',
          padding: '10px',
          border: '1px solid #333',
          backgroundColor: '#f9f9f9'
        }}>
          <h3>Информация о месте: {selectedSeatInfo.seat.id}</h3>
          <p><strong>Тип:</strong> {selectedSeatInfo.seat.type}</p>
          <p><strong>Свободно на отрезках:</strong></p>
          <ul>
            {selectedSeatInfo.freeSegments.map((seg, idx) => <li key={idx}>{seg}</li>)}
          </ul>
          <p><strong>Занято на отрезках:</strong></p>
          <ul>
            {selectedSeatInfo.busySegments.map((seg, idx) => <li key={idx}>{seg}</li>)}
          </ul>
        </div>
      )}
    </div>
  );
};

export default TrainResults;
