import React, { useState } from 'react';
import SeatSelectionModal from './SeatSelectionModal';

const TrainResults = ({ results }) => {
  const [selectedTrain, setSelectedTrain] = useState(null);

  const handleOpenSeats = (train) => {
    setSelectedTrain(train);
  };

  const handleCloseModal = () => {
    setSelectedTrain(null);
  };

  const [bookings, setBookings] = useState([]);

  const handleConfirmSeats = (selectedSeats) => {
    const newBooking = {
      train: selectedTrain.number,
      seats: selectedSeats.map(s => s.id),
      types: selectedSeats.map(s => s.type)
    };
    setBookings(prev => [...prev, newBooking]);
  };

  if (results.length === 0) {
    return <p>Нет доступных поездов по заданным параметрам.</p>;
  }

  return (
    <div>
      <h2>Результаты поиска</h2>
      {results.map((train, index) => (
        <div key={index} style={{ border: '1px solid #ccc', marginBottom: '10px', padding: '10px' }}>
          <p><strong>Поезд:</strong> {train.number}</p>
          <p><strong>Время отправления:</strong> {train.departure}</p>
          <p><strong>Время прибытия:</strong> {train.arrival}</p>
          <p><strong>Свободные места:</strong> {
            train.seats.filter(s => !s.booked).length
        }</p>
          <button onClick={() => handleOpenSeats(train)}>Выбрать места</button>
        </div>
      ))}

      {selectedTrain && (
        <SeatSelectionModal
          train={selectedTrain}
          onClose={handleCloseModal}
          onConfirm={handleConfirmSeats}
        />
      )}
    </div>
  );
};

export default TrainResults;
